import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { authAPI, clubsAPI, reservationsAPI } from '../utils/api';
import { isAuthenticated, getCurrentUser, logoutUser } from '../utils/auth';
import reservationService from '../utils/reservationService';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [clubs, setClubs] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [profileData, setProfileData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    username: ''
  });
  const [profileError, setProfileError] = useState(null);
  const [profileLoading, setProfileLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect if not authenticated
    if (!isAuthenticated()) {
      console.log("Dashboard: User not authenticated, redirecting to login");
      navigate('/login', { replace: true });
      return;
    }

    // Try to get cached user data first
    const cachedUser = getCurrentUser();
    if (cachedUser) {
      setUser(cachedUser);
      setProfileData({
        first_name: cachedUser.first_name || '',
        last_name: cachedUser.last_name || '',
        email: cachedUser.email || '',
        username: cachedUser.username || ''
      });
    }

    // Fetch fresh data
    fetchUserData();
  }, [navigate]);

  const fetchUserData = async () => {
    setLoading(true);
    setError(null);

    try {
      // Fetch user data
      const userData = await authAPI.getCurrentUser();
      setUser(userData);
      setProfileData({
        first_name: userData.first_name || '',
        last_name: userData.last_name || '',
        email: userData.email || '',
        username: userData.username || ''
      });

      // Fetch user's clubs (if any)
      try {
        const clubsData = await clubsAPI.getClubsByOwner();
        setClubs(clubsData || []);
      } catch (clubErr) {
        console.error('Error fetching clubs:', clubErr);
        setClubs([]);
      }

      // Fetch user's reservations (if any)
      try {
        console.log('Fetching user reservations...');
        const reservationsData = await reservationService.getMyReservations();
        console.log('Reservations data received:', reservationsData);
        setReservations(Array.isArray(reservationsData) ? reservationsData : []);
      } catch (resErr) {
        console.error('Error fetching reservations:', resErr);
        // Don't show an error for missing features, just set empty array
        setReservations([]);
      }

      setLoading(false);
    } catch (err) {
      console.error('Error fetching user data:', err);
      setError('Failed to load your profile. Please try again.');
      setLoading(false);

      // If unauthorized, redirect to login
      if (err.status === 401) {
        logoutUser();
      }
    }
  };

  const handleLogout = () => {
    logoutUser();
    navigate('/login');
  };

  const handleCancelReservation = async (reservationId) => {
    if (!reservationId) return;

    if (window.confirm('Are you sure you want to cancel this reservation?')) {
      try {
        await reservationService.cancelReservation(reservationId);
        // Re-fetch reservations data to update UI
        fetchUserData();
      } catch (error) {
        console.error('Error canceling reservation:', error);
        setError('Failed to cancel reservation. Please try again.');
      }
    }
  };

  const handleCreateClub = () => {
    console.log("Navigate to create club page");
    try {
      // Check if user is authenticated and is a club owner
      if (!isAuthenticated()) {
        console.error("User is not authenticated");
        navigate('/login');
        return;
      }

      if (!user?.is_club_owner) {
        console.error("User is not a club owner");
        // Still navigate, the CreateClub component will handle this case
      }

      navigate('/create-club');
    } catch (error) {
      console.error("Error navigating to create club:", error);
    }
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setProfileError(null);
    setProfileLoading(true);

    try {
      const updatedUser = await authAPI.updateProfile(profileData);
      setUser(updatedUser);
      setEditMode(false);

      // Update the local storage with new user data
      const userData = getCurrentUser();
      if (userData) {
        const updatedUserData = {
          ...userData,
          ...profileData
        };
        localStorage.setItem('sports_app_user', JSON.stringify(updatedUserData));
      }
    } catch (err) {
      setProfileError(err.message || 'Failed to update profile');
      console.error('Error updating profile:', err);
    } finally {
      setProfileLoading(false);
    }
  };

  if (loading && !user) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (error && !user) {
    return (
      <div className="container">
        <div className="error-box">
          <p>{error}</p>
          <button 
            onClick={() => fetchUserData()} 
            className="retry-button"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container dashboard">
      <h1 className="dashboard-title">Dashboard</h1>

      {/* User Info */}
      <div className="card profile-card">
        <h2 className="card-title">Your Profile</h2>
        {editMode ? (
          <form onSubmit={handleProfileSubmit}>
            {profileError && (
              <div className="error-message">
                {profileError}
              </div>
            )}

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="first_name">First Name</label>
                <input
                  type="text"
                  id="first_name"
                  name="first_name"
                  value={profileData.first_name}
                  onChange={handleProfileChange}
                  className="form-control"
                  disabled={profileLoading}
                />
              </div>

              <div className="form-group">
                <label htmlFor="last_name">Last Name</label>
                <input
                  type="text"
                  id="last_name"
                  name="last_name"
                  value={profileData.last_name}
                  onChange={handleProfileChange}
                  className="form-control"
                  disabled={profileLoading}
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={profileData.email}
                onChange={handleProfileChange}
                className="form-control"
                disabled={true} // Email shouldn't be editable
              />
            </div>

            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                name="username"
                value={profileData.username}
                onChange={handleProfileChange}
                className="form-control"
                disabled={true} // Username shouldn't be editable
              />
            </div>

            <div className="button-group">
              <button
                type="submit"
                className="btn btn-primary"
                disabled={profileLoading}
              >
                {profileLoading ? 'Saving...' : 'Save Changes'}
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => setEditMode(false)}
                disabled={profileLoading}
              >
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <div className="profile-info">
            <div className="profile-avatar">
              {user?.username?.charAt(0).toUpperCase() || 'U'}
            </div>
            <div className="profile-details">
              <p className="profile-username">{user?.username || 'User'}</p>
              <p className="profile-email">{user?.email || 'user@example.com'}</p>
              {user?.first_name && user?.last_name && (
                <p className="profile-name">{user.first_name} {user.last_name}</p>
              )}
              <div className="button-group">
                <button 
                  className="btn btn-primary"
                  onClick={() => setEditMode(true)}
                >
                  Edit Profile
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Your Clubs */}
      <div className="card clubs-card">
        <div className="card-header">
          <h2 className="card-title">Your Clubs</h2>
          {user?.is_club_owner && (
            <button 
              onClick={handleCreateClub}
              className="btn btn-primary"
            >
              Create Club
            </button>
          )}
        </div>

        {clubs.length === 0 ? (
          <div className="empty-state">
            <p>
              {user?.is_club_owner 
                ? "You haven't created any clubs yet." 
                : "You haven't joined any clubs yet."}
            </p>
            {user?.is_club_owner ? (
              <button 
                onClick={handleCreateClub}
                className="btn btn-primary"
              >
                Create Your First Club
              </button>
            ) : (
              <button 
                onClick={() => navigate('/')}
                className="btn btn-primary"
              >
                Find Clubs
              </button>
            )}
          </div>
        ) : (
          <div className="table-responsive">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Club Name</th>
                  <th>Location</th>
                  <th>Your Role</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {clubs.map((club) => (
                  <tr key={club.id}>
                    <td className="font-medium">{club.name}</td>
                    <td>{club.town || 'N/A'}</td>
                    <td>Owner</td>
                    <td className="action-links">
                      <a 
                        onClick={() => navigate(`/clubs/${club.id}`)} 
                        className="action-link view-link"
                      >
                        View
                      </a>
                      <a 
                        onClick={() => navigate(`/edit-club/${club.id}`)} 
                        className="action-link edit-link"
                      >
                        Edit
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
        </div>
        )}
      </div>

      {/* Upcoming Reservations */}
      <div className="card reservations-card">
        <h2 className="card-title">Upcoming Reservations</h2>

        {reservations.length === 0 ? (
          <div className="empty-state">
            <p>You don't have any upcoming reservations.</p>
            <button 
              onClick={() => navigate('/')}
              className="btn btn-primary"
            >
              Find Courts to Book
            </button>
          </div>
        ) : (
          <div className="table-responsive">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Club</th>
                  <th>Date</th>
                  <th>Time</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {Array.isArray(reservations) && reservations.map((reservation) => (
                  <tr key={reservation.id || Math.random()}>
                    <td className="font-medium">{reservation.club_name || 'Unknown Club'}</td>
                    <td>{reservation.date ? new Date(reservation.date).toLocaleDateString() : 'N/A'}</td>
                    <td>{reservation.start_time || 'N/A'} - {reservation.end_time || 'N/A'}</td>
                    <td>
                      <span 
                        className={`status-badge ${reservation.status}`}
                      >
                        {reservation.status || 'unknown'}
                      </span>
                    </td>
                    <td>
                      {(reservation.status !== 'cancelled' && reservation.status) && (
                        <button 
                          onClick={() => handleCancelReservation(reservation.id)}
                          className="cancel-button"
                        >
                          Cancel
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
