import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ClubDetails() {
  const { id } = useParams();
  const [club, setClub] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeImageIndex, setActiveImageIndex] = useState(0);
  
  // User state
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(null);
  
  // Review/comment state
  const [reviewText, setReviewText] = useState('');
  const [rating, setRating] = useState(5);
  const [commentText, setCommentText] = useState('');
  const [reviews, setReviews] = useState([]);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    // Check if user is logged in
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setIsLoggedIn(true);
      setUserId(storedUserId);
    }
    
    fetchClubDetails();
  }, [id]);

  const fetchClubDetails = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/v1/clubs/${id}`);
      
      if (response.ok) {
        const clubData = await response.json();
        setClub(clubData);
        
        // For now, we'll use mock data for reviews and comments
        // In a real app, you would fetch these from the API
        setReviews([
          { id: 1, user: { username: 'user1' }, rating: 4, comment: 'Great club, highly recommended!', created_at: '2023-01-01' },
          { id: 2, user: { username: 'user2' }, rating: 5, comment: 'Excellent facilities and coaches', created_at: '2023-01-02' }
        ]);
        
        setComments([
          { id: 1, user: { username: 'user3' }, content: 'Do you offer classes for beginners?', created_at: '2023-01-03' },
          { id: 2, user: { username: 'user4' }, content: 'What are your weekend hours?', created_at: '2023-01-04' }
        ]);
      } else {
        setError('Could not load club details');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
      console.error('Error fetching club details:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    // In a real app, you would send the review to the API
    alert('Review submission will be implemented in a future version');
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    // In a real app, you would send the comment to the API
    alert('Comment submission will be implemented in a future version');
  };

  const nextImage = () => {
    if (club?.pictures?.length > 0) {
      setActiveImageIndex((prevIndex) => 
        prevIndex === club.pictures.length - 1 ? 0 : prevIndex + 1
      );
    }
  };

  const prevImage = () => {
    if (club?.pictures?.length > 0) {
      setActiveImageIndex((prevIndex) => 
        prevIndex === 0 ? club.pictures.length - 1 : prevIndex - 1
      );
    }
  };

  if (loading) {
    return <div className="card">Loading club details...</div>;
  }

  if (error) {
    return <div className="card error-message">{error}</div>;
  }

  if (!club) {
    return <div className="card">Club not found</div>;
  }

  return (
    <div className="club-details">
      <div className="card">
        <h1>{club.name}</h1>
        
        <div className="club-gallery">
          {club.pictures && club.pictures.length > 0 ? (
            <div className="gallery-container">
              <img 
                src={club.pictures[activeImageIndex]} 
                alt={`${club.name} - Image ${activeImageIndex + 1}`}
                className="main-image" 
              />
              
              {club.pictures.length > 1 && (
                <div className="gallery-controls">
                  <button onClick={prevImage} className="gallery-button">&lt;</button>
                  <div className="image-counter">{activeImageIndex + 1} / {club.pictures.length}</div>
                  <button onClick={nextImage} className="gallery-button">&gt;</button>
                </div>
              )}
              
              {club.pictures.length > 1 && (
                <div className="thumbnail-container">
                  {club.pictures.map((pic, index) => (
                    <img 
                      key={index}
                      src={pic} 
                      alt={`Thumbnail ${index + 1}`}
                      className={`thumbnail ${index === activeImageIndex ? 'active' : ''}`}
                      onClick={() => setActiveImageIndex(index)}
                    />
                  ))}
                </div>
              )}
            </div>
          ) : (
            <img 
              src="https://via.placeholder.com/800x400?text=No+Images" 
              alt="No images available" 
              className="main-image"
            />
          )}
        </div>
        
        <div className="club-info">
          <div className="info-section">
            <h2>Club Information</h2>
            <p><strong>Location:</strong> {club.town}</p>
            {club.address && <p><strong>Address:</strong> {club.address}</p>}
            <p><strong>Telephone:</strong> {club.telephone}</p>
            <p><strong>Hourly Price:</strong> ${club.hourly_price}</p>
            {club.website && (
              <p>
                <strong>Website:</strong> 
                <a href={club.website} target="_blank" rel="noopener noreferrer">
                  {club.website}
                </a>
              </p>
            )}
            {club.social_media && <p><strong>Social Media:</strong> {club.social_media}</p>}
            {club.owner_name && <p><strong>Owner:</strong> {club.owner_name}</p>}
          </div>
          
          {club.description && (
            <div className="info-section">
              <h2>Description</h2>
              <p>{club.description}</p>
            </div>
          )}
        </div>
      </div>
      
      <div className="card">
        <h2>Reviews</h2>
        <div className="rating-summary">
          <div className="rating-big">
            {club.average_rating ? (
              <>
                <span className="rating-number">{club.average_rating.toFixed(1)}</span>
                <span className="rating-stars">
                  {Array(5).fill(null).map((_, i) => (
                    <span key={i} className={i < Math.round(club.average_rating) ? 'star filled' : 'star'}>★</span>
                  ))}
                </span>
              </>
            ) : (
              <span>No ratings yet</span>
            )}
          </div>
          <div className="rating-count">Based on {club.reviews_count || 0} reviews</div>
        </div>
        
        {isLoggedIn && (
          <div className="write-review">
            <h3>Write a Review</h3>
            <form onSubmit={handleReviewSubmit}>
              <div className="rating-select">
                <label>Your Rating:</label>
                <div className="rating-input">
                  {[5, 4, 3, 2, 1].map((star) => (
                    <label key={star}>
                      <input 
                        type="radio" 
                        name="rating" 
                        value={star} 
                        checked={rating === star}
                        onChange={() => setRating(star)}
                      />
                      <span className="star">★</span>
                    </label>
                  ))}
                </div>
              </div>
              <div className="form-group">
                <label htmlFor="review">Your Review:</label>
                <textarea 
                  id="review" 
                  rows="4"
                  value={reviewText}
                  onChange={(e) => setReviewText(e.target.value)}
                  required
                ></textarea>
              </div>
              <button type="submit">Submit Review</button>
            </form>
          </div>
        )}
        
        <div className="reviews-list">
          {reviews.length > 0 ? (
            reviews.map(review => (
              <div key={review.id} className="review">
                <div className="review-header">
                  <span className="review-author">{review.user.username}</span>
                  <span className="review-date">{new Date(review.created_at).toLocaleDateString()}</span>
                  <span className="review-rating">
                    {Array(5).fill(null).map((_, i) => (
                      <span key={i} className={i < review.rating ? 'star filled' : 'star'}>★</span>
                    ))}
                  </span>
                </div>
                <div className="review-content">{review.comment}</div>
              </div>
            ))
          ) : (
            <p>No reviews yet. Be the first to leave a review!</p>
          )}
        </div>
      </div>
      
      <div className="card">
        <h2>Comments</h2>
        
        {isLoggedIn && (
          <div className="write-comment">
            <h3>Leave a Comment</h3>
            <form onSubmit={handleCommentSubmit}>
              <div className="form-group">
                <label htmlFor="comment">Your Comment:</label>
                <textarea 
                  id="comment" 
                  rows="3"
                  value={commentText}
                  onChange={(e) => setCommentText(e.target.value)}
                  required
                ></textarea>
              </div>
              <button type="submit">Post Comment</button>
            </form>
          </div>
        )}
        
        <div className="comments-list">
          {comments.length > 0 ? (
            comments.map(comment => (
              <div key={comment.id} className="comment">
                <div className="comment-header">
                  <span className="comment-author">{comment.user.username}</span>
                  <span className="comment-date">{new Date(comment.created_at).toLocaleDateString()}</span>
                </div>
                <div className="comment-content">{comment.content}</div>
              </div>
            ))
          ) : (
            <p>No comments yet. Be the first to leave a comment!</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default ClubDetails;
