import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { clubsAPI } from '../utils/api';
import { isAuthenticated } from '../utils/auth';

// Club Card Component
const ClubCard = ({ club }) => {
  return (
    <div className="bg-gradient-to-b from-orange-600 to-orange-700 overflow-hidden rounded-lg shadow-lg transition-all hover:shadow-xl">
      <div className="p-4 font-bold text-white">
        <h3 className="text-xl">{club.name}</h3>
      </div>
      <div className="bg-gray-800 p-3 text-gray-300">
        <div className="flex items-center mb-2">
          <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
            <path fillRule="evenodd" d="M5.05 4.05a7 7 0 119.9 9.9L10 18.9l-4.95-4.95a7 7 0 010-9.9zM10 11a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd"></path>
          </svg>
          <span>{club.town || 'Unknown location'}</span>
        </div>
        <div className="flex items-center mb-2">
          <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
            <path d="M2 10.5a1.5 1.5 0 113 0v6a1.5 1.5 0 01-3 0v-6zM6 10.333v5.43a2 2 0 001.106 1.79l.05.025A4 4 0 008.943 18h5.416a2 2 0 001.962-1.608l1.2-6A2 2 0 0015.56 8H12V4a2 2 0 00-2-2 1 1 0 00-1 1v.667a4 4 0 01-.8 2.4L6.8 7.933a4 4 0 00-.8 2.4z"></path>
          </svg>
          <span>â‚¬{club.hourly_price}/hour</span>
        </div>
        <div className="flex items-center mb-2">
          <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
            <path d="M2 3a1 1 0 011-1h2.153a1 1 0 01.986.836l.74 4.435a1 1 0 01-.54 1.06l-1.548.773a11.037 11.037 0 006.105 6.105l.774-1.548a1 1 0 011.059-.54l4.435.74a1 1 0 01.836.986V17a1 1 0 01-1 1h-2C7.82 18 2 12.18 2 5V3z"></path>
          </svg>
          <span>{club.telephone}</span>
        </div>
        <Link to={`/clubs/${club.id}`} className="w-full bg-orange-600 hover:bg-orange-700 text-white py-2 px-4 rounded mt-2 transition-colors block text-center">
          View Details
        </Link>
      </div>
    </div>
  );
};

const HomePage = () => {
  // State for clubs data
  const [clubs, setClubs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  
  // State for search filters
  const [filters, setFilters] = useState({
    city: '',
    sport: '',
    facility: ''
  });
  
  // Cities and sports for dropdown options
  const cities = ['Sofia', 'Plovdiv', 'Varna', 'Burgas', 'Ruse', 'Stara Zagora', 'Targovishte'];
  const sports = ['Tennis', 'Football', 'Basketball', 'Volleyball', 'Swimming'];
  const facilities = ['All Facilities', 'Indoor', 'Outdoor', 'With Lighting', 'With Showers'];
  
  // Fetch clubs on component mount
  useEffect(() => {
    fetchClubs();
  }, []);
  
  // Function to fetch clubs data with optional filters
  const fetchClubs = async (filterParams = {}) => {
    setLoading(true);
    setError(null);
    
    try {
      // Convert UI filter values to API filter parameters
      const apiFilters = {};
      if (filterParams.city) apiFilters.town = filterParams.city;
      if (filterParams.sport) apiFilters.sport_type = filterParams.sport;
      
      // Handle facility filters specifically
      if (filterParams.facility === 'Indoor') apiFilters.is_indoor = true;
      if (filterParams.facility === 'Outdoor') apiFilters.is_indoor = false;
      if (filterParams.facility === 'With Lighting') apiFilters.has_lighting = true;
      if (filterParams.facility === 'With Showers') apiFilters.has_showers = true;
      
      // Call API with filters
      const data = await clubsAPI.getAllClubs(apiFilters);
      
      // If we have data, set it
      if (data && data.length > 0) {
        setClubs(data);
      } else {
        // If no data from API and no filters are applied, try to use mock data
        if (Object.keys(filterParams).length === 0) {
          console.log("Using mock data as API returned no results");
          setClubs([
            { id: 1, name: 'TK Team Kunchev', town: 'Sofia', telephone: '+359 888 123 456', hourly_price: 25 },
            { id: 2, name: 'Tennis club Tangra 95', town: 'Targovishte', telephone: '+359 888 234 567', hourly_price: 20 },
            { id: 3, name: 'Komak Sport', town: 'Sofia', telephone: '+359 888 345 678', hourly_price: 30 },
            { id: 4, name: 'Sea Point Aralia', town: 'Varna', telephone: '+359 888 456 789', hourly_price: 15 },
            { id: 5, name: 'Tennis club DIM Team', town: 'Plovdiv', telephone: '+359 888 567 890', hourly_price: 22 },
          ]);
        } else {
          // If user applied filters but got no results
          setClubs([]);
        }
      }
      
      setLoading(false);
    } catch (err) {
      console.error('Error fetching clubs:', err);
      setError('Failed to load clubs. Please try again.');
      setLoading(false);
    }
  };
  
  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    const newFilters = { ...filters, [name]: value };
    setFilters(newFilters);
  };
  
  // Handle search button click
  const handleSearch = () => {
    fetchClubs(filters);
  };

  return (
    <div className="flex flex-col">
      {/* Hero Image and Search Section */}
      <div 
        className="relative flex items-center justify-center h-64 bg-cover bg-center"
        style={{ 
          backgroundImage: 'linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.7)), url("https://images.unsplash.com/photo-1545809074-59472b3f5ecc?ixlib=rb-4.0.3&auto=format&fit=crop&w=1950&q=80")' 
        }}
      >
        <div className="container mx-auto px-4">
          <div className="max-w-3xl mx-auto text-center">
            <h1 className="text-3xl font-bold text-white mb-6">Find and Book Sports Facilities</h1>
            
            {/* Search Form */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-2">
              <div className="relative">
                <select 
                  name="city"
                  value={filters.city}
                  onChange={handleFilterChange}
                  className="appearance-none w-full bg-white border border-gray-300 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                >
                  <option value="">Select City</option>
                  {cities.map((city) => (
                    <option key={city} value={city}>{city}</option>
                  ))}
                </select>
                <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
                  <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/></svg>
                </div>
              </div>
              
              <div className="relative">
                <select
                  name="sport"
                  value={filters.sport}
                  onChange={handleFilterChange}
                  className="appearance-none w-full bg-white border border-gray-300 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                >
                  <option value="">All Sports</option>
                  {sports.map((sport) => (
                    <option key={sport} value={sport}>{sport}</option>
                  ))}
                </select>
                <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
                  <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/></svg>
                </div>
              </div>
              
              <div className="relative">
                <select
                  name="facility"
                  value={filters.facility}
                  onChange={handleFilterChange}
                  className="appearance-none w-full bg-white border border-gray-300 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500"
                >
                  <option value="">All Facilities</option>
                  {facilities.slice(1).map((facility) => (
                    <option key={facility} value={facility}>{facility}</option>
                  ))}
                </select>
                <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
                  <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/></svg>
                </div>
              </div>
              
              <div className="relative">
                <button 
                  onClick={handleSearch}
                  className="bg-orange-600 hover:bg-orange-700 text-white w-full font-bold py-3 px-4 rounded transition-colors"
                >
                  Search
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Clubs Section */}
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-white">
            {Object.values(filters).some(f => f !== '') ? 'Search Results' : 'Featured Clubs'}
          </h2>
          {/* Removed View All link since it's not needed */}
        </div>
        
        {loading ? (
          <div className="text-center py-10">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-orange-500 mx-auto"></div>
            <p className="mt-2 text-gray-400">Loading clubs...</p>
          </div>
        ) : error ? (
          <div className="text-center py-10 text-red-500">{error}</div>
        ) : clubs.length === 0 ? (
          <div className="text-center py-10">
            <p className="text-gray-400 mb-4">No clubs found matching your criteria.</p>
            <button 
              onClick={() => {
                setFilters({ city: '', sport: '', facility: '' });
                fetchClubs({});
              }}
              className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded text-white"
            >
              Reset Filters
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {clubs.map(club => (
              <ClubCard key={club.id} club={club} />
            ))}
          </div>
        )}
      </div>
      
      {/* Call to Action */}
      <div className="bg-gradient-to-r from-orange-600 to-orange-700 py-12">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl font-bold text-white mb-4">Ready to join?</h2>
          <p className="text-white mb-6 max-w-2xl mx-auto">Create an account to book sports facilities, join clubs, and participate in tournaments.</p>
          {!isAuthenticated() && (
            <div className="flex flex-col sm:flex-row justify-center gap-4">
              <Link to="/register" className="bg-white text-orange-600 hover:bg-gray-100 font-bold py-3 px-6 rounded-full transition-colors">
                Register Now
              </Link>
              <Link to="/login" className="bg-transparent border-2 border-white text-white hover:bg-white hover:text-orange-600 font-bold py-3 px-6 rounded-full transition-colors">
                Log In
              </Link>
            </div>
          )}
          {isAuthenticated() && (
            <button 
              onClick={() => navigate('/dashboard')}
              className="bg-white text-orange-600 hover:bg-gray-100 font-bold py-3 px-6 rounded-full transition-colors"
            >
              Go to Your Dashboard
            </button>
          )}
        </div>
      </div>
      
      {/* Sports Icons */}
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-center flex-wrap gap-8">
          {sports.map(sport => (
            <div key={sport} className="text-center">
              <div className="w-16 h-16 bg-gray-800 rounded-full flex items-center justify-center mx-auto mb-2">
                <svg className="w-8 h-8 text-orange-500" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd"></path>
                </svg>
              </div>
              <span className="text-gray-400 capitalize">{sport}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
