import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

// Simple App component for demonstration
const App = () => {
  return (
    <div style={{ 
      minHeight: '100vh', 
      backgroundColor: '#121212',
      color: '#e1e1e1',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px'
    }}>
      <div style={{
        maxWidth: '800px',
        backgroundColor: '#1e1e1e',
        borderRadius: '10px',
        padding: '30px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
      }}>
        <h1 style={{ color: '#3a86ff', marginBottom: '20px' }}>
          Sports Community Platform
        </h1>
        <p style={{ fontSize: '18px', lineHeight: '1.6' }}>
          The React frontend has been successfully created with dark theme styling inspired by clickandplay.bg.
        </p>
        <div style={{ 
          marginTop: '30px',
          display: 'flex',
          gap: '15px'
        }}>
          <a href="/login" style={{
            backgroundColor: '#3a86ff',
            color: 'white',
            padding: '10px 20px',
            borderRadius: '5px',
            textDecoration: 'none',
            fontWeight: 'bold'
          }}>
            Login
          </a>
          <a href="/register" style={{
            backgroundColor: 'transparent',
            color: '#3a86ff',
            padding: '10px 20px',
            borderRadius: '5px',
            textDecoration: 'none',
            fontWeight: 'bold',
            border: '1px solid #3a86ff'
          }}>
            Register
          </a>
        </div>
      </div>
    </div>
  );
};

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
