import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ClubDetails() {
  const { id } = useParams();
  const [club, setClub] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeImageIndex, setActiveImageIndex] = useState(0);
  
  // User state
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(null);
  
  // Review/comment state
  const [reviewText, setReviewText] = useState('');
  const [rating, setRating] = useState(5);
  const [commentText, setCommentText] = useState('');
  const [reviews, setReviews] = useState([]);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    // Check if user is logged in
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setIsLoggedIn(true);
      setUserId(storedUserId);
    }
    
    fetchClubDetails();
    // We'll also fetch the detailed club information that includes ratings and comments
    fetchClubDetailedInfo();
  }, [id]);

  const fetchClubDetails = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/v1/clubs/${id}`);
      
      if (response.ok) {
        const clubData = await response.json();
        console.log('Club data:', clubData); // Debug log
        
        // Ensure pictures is an array
        if (clubData.pictures === null || clubData.pictures === undefined) {
          clubData.pictures = [];
        } else if (typeof clubData.pictures === 'string') {
          try {
            clubData.pictures = JSON.parse(clubData.pictures);
          } catch (e) {
            console.error('Error parsing pictures JSON:', e);
            clubData.pictures = [];
          }
        }
        
        setClub(clubData);
      } else {
        setError('Could not load club details');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
      console.error('Error fetching club details:', error);
    }
  };
  
  const fetchClubDetailedInfo = async () => {
    try {
      const detailsResponse = await fetch(`/api/v1/clubs/${id}/details`);
      
      if (detailsResponse.ok) {
        const detailedData = await detailsResponse.json();
        
        // Update club with more detailed information
        setClub(prev => ({
          ...prev,
          ...detailedData
        }));
        
        // In a full implementation, you would also fetch reviews and comments here
        // For now, we'll use mock data
        setReviews([
          { id: 1, user: { username: 'user1' }, rating: 4, comment: 'Great club, highly recommended!', created_at: '2023-01-01' },
          { id: 2, user: { username: 'user2' }, rating: 5, comment: 'Excellent facilities and coaches', created_at: '2023-01-02' }
        ]);
        
        setComments([
          { id: 1, user: { username: 'user3' }, content: 'Do you offer classes for beginners?', created_at: '2023-01-03' },
          { id: 2, user: { username: 'user4' }, content: 'What are your weekend hours?', created_at: '2023-01-04' }
        ]);
      }
    } catch (error) {
      console.error('Error fetching detailed club info:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    // In a real app, you would send the review to the API
    alert('Review submission will be implemented in a future version');
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    // In a real app, you would send the comment to the API
    alert('Comment submission will be implemented in a future version');
  };

  const nextImage = () => {
    if (club?.pictures?.length > 0) {
      setActiveImageIndex((prevIndex) => 
        prevIndex === club.pictures.length - 1 ? 0 : prevIndex + 1
      );
    }
  };

  const prevImage = () => {
    if (club?.pictures?.length > 0) {
      setActiveImageIndex((prevIndex) => 
        prevIndex === 0 ? club.pictures.length - 1 : prevIndex - 1
      );
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-[50vh]">
        <div className="card p-8 flex flex-col items-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary mb-4"></div>
          <p className="text-gray-300">Loading club details...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-[50vh]">
        <div className="card p-8 border border-red-600">
          <h2 className="text-red-500 text-xl mb-2">Error</h2>
          <p className="text-gray-300">{error}</p>
        </div>
      </div>
    );
  }

  if (!club) {
    return (
      <div className="flex justify-center items-center min-h-[50vh]">
        <div className="card p-8">
          <h2 className="text-xl mb-2">Not Found</h2>
          <p className="text-gray-300">Club not found or has been removed.</p>
        </div>
      </div>
    );
  }

  // Check if picture URLs need to be adjusted to include the base URL
  const getImageUrl = (url) => {
    if (!url) return "https://via.placeholder.com/800x400?text=No+Image";
    if (url.startsWith('http')) return url;
    return url.startsWith('/') ? url : `/${url}`;
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8 space-y-8">
      <div className="card overflow-hidden">
        <div className="md:flex">
          <div className="md:w-2/3 relative">
            {club.pictures && club.pictures.length > 0 ? (
              <>
                <div className="relative aspect-[16/9] overflow-hidden bg-gray-900">
                  <img 
                    src={getImageUrl(club.pictures[activeImageIndex])} 
                    alt={`${club.name} - Image ${activeImageIndex + 1}`}
                    className="w-full h-full object-cover" 
                    onError={(e) => {
                      console.error('Image failed to load:', e.target.src);
                      e.target.src = "https://via.placeholder.com/800x400?text=Image+Not+Found";
                    }}
                  />
                  
                  {club.pictures.length > 1 && (
                    <>
                      <button 
                        onClick={prevImage} 
                        className="absolute left-2 top-1/2 -translate-y-1/2 bg-black/50 text-white p-2 rounded-full hover:bg-black/70 transition-colors"
                        aria-label="Previous image"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                        </svg>
                      </button>
                      <button 
                        onClick={nextImage} 
                        className="absolute right-2 top-1/2 -translate-y-1/2 bg-black/50 text-white p-2 rounded-full hover:bg-black/70 transition-colors"
                        aria-label="Next image"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                        </svg>
                      </button>
                      
                      <div className="absolute bottom-2 right-2 bg-black/70 text-white px-3 py-1 rounded-full text-sm">
                        {activeImageIndex + 1} / {club.pictures.length}
                      </div>
                    </>
                  )}
                </div>
                
                {club.pictures.length > 1 && (
                  <div className="flex overflow-x-auto gap-2 p-2 bg-gray-800">
                    {club.pictures.map((pic, index) => (
                      <img 
                        key={index}
                        src={getImageUrl(pic)} 
                        alt={`Thumbnail ${index + 1}`}
                        className={`w-20 h-14 object-cover rounded cursor-pointer transition-all 
                                  ${index === activeImageIndex ? 'ring-2 ring-primary' : 'opacity-70 hover:opacity-100'}`}
                        onClick={() => setActiveImageIndex(index)}
                        onError={(e) => {
                          e.target.src = "https://via.placeholder.com/160x120?text=Thumb";
                        }}
                      />
                    ))}
                  </div>
                )}
              </>
            ) : (
              <div className="aspect-[16/9] bg-gray-800 flex items-center justify-center">
                <div className="text-center text-gray-400">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                  </svg>
                  <p>No images available</p>
                </div>
              </div>
            )}
          </div>
          
          <div className="md:w-1/3 p-6">
            <h1 className="text-3xl font-bold mb-4 text-gray-100">{club.name}</h1>
            
            <div className="flex items-center mb-4">
              <div className="flex text-yellow-400 mr-2">
                {Array(5).fill(null).map((_, i) => (
                  <span key={i} className={i < Math.round(club?.average_rating || 0) ? 'text-yellow-400' : 'text-gray-600'}>★</span>
                ))}
              </div>
              <span className="text-sm text-gray-400">
                {club?.average_rating ? club.average_rating.toFixed(1) : 'No ratings'} 
                {club?.reviews_count ? ` (${club.reviews_count} reviews)` : ''}
              </span>
            </div>
            
            <div className="space-y-3 text-gray-300">
              <div className="flex items-start">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-primary mt-0.5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                </svg>
                <div>
                  <p className="font-medium">{club.town}</p>
                  {club.address && <p className="text-gray-400 text-sm">{club.address}</p>}
                </div>
              </div>
              
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-primary" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                </svg>
                <p>{club.telephone}</p>
              </div>
              
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-primary" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <p>${club.hourly_price}/hour</p>
              </div>
              
              {club.website && (
                <div className="flex items-center">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-primary" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12a9 9 0 01-9 9m9-9a9 9 0 00-9-9m9 9H3m9 9a9 9 0 01-9-9m9 9c1.657 0 3-4.03 3-9s-1.343-9-3-9m0 18c-1.657 0-3-4.03-3-9s1.343-9 3-9m-9 9a9 9 0 019-9" />
                  </svg>
                  <a href={club.website} target="_blank" rel="noopener noreferrer" className="text-primary hover:text-primary-light transition-colors">
                    Website
                  </a>
                </div>
              )}
              
              {club.owner_name && (
                <div className="flex items-center">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-primary" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                  </svg>
                  <p>Owner: {club.owner_name}</p>
                </div>
              )}
            </div>
          </div>
        </div>
        
        {club.description && (
          <div className="p-6 border-t border-gray-800">
            <h2 className="text-xl font-semibold mb-3 text-gray-100">Description</h2>
            <p className="text-gray-300 whitespace-pre-line">{club.description}</p>
          </div>
        )}
      </div>
      
      <div className="card">
        <h2 className="text-xl font-semibold mb-4 text-gray-100">Reviews</h2>
        
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center">
            <div className="text-4xl font-bold text-gray-100 mr-3">{club.average_rating ? club.average_rating.toFixed(1) : "0.0"}</div>
            <div>
              <div className="flex text-yellow-400 mb-1">
                {Array(5).fill(null).map((_, i) => (
                  <span key={i} className={i < Math.round(club?.average_rating || 0) ? 'text-yellow-400' : 'text-gray-600'}>★</span>
                ))}
              </div>
              <div className="text-sm text-gray-400">Based on {club.reviews_count || 0} reviews</div>
            </div>
          </div>
          
          {isLoggedIn && (
            <button onClick={() => document.getElementById('review-form').scrollIntoView({ behavior: 'smooth' })}
                    className="btn btn-primary">
              Write a Review
            </button>
          )}
        </div>
        
        {isLoggedIn && (
          <div id="review-form" className="bg-dark-lighter rounded-lg p-4 mb-6">
            <h3 className="text-lg font-medium mb-3 text-gray-200">Write a Review</h3>
            <form onSubmit={handleReviewSubmit}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-300 mb-2">Your Rating:</label>
                <div className="flex text-2xl">
                  {[5, 4, 3, 2, 1].map((star) => (
                    <label key={star} className="cursor-pointer px-1">
                      <input 
                        type="radio" 
                        name="rating" 
                        value={star} 
                        checked={rating === star}
                        onChange={() => setRating(star)}
                        className="sr-only"
                      />
                      <span className={`${rating >= star ? 'text-yellow-400' : 'text-gray-600'} hover:text-yellow-300 transition-colors`}>★</span>
                    </label>
                  ))}
                </div>
              </div>
              <div className="form-group">
                <label htmlFor="review" className="label">Your Review:</label>
                <textarea 
                  id="review" 
                  rows="4"
                  value={reviewText}
                  onChange={(e) => setReviewText(e.target.value)}
                  required
                  className="input"
                  placeholder="Share your experience..."
                ></textarea>
              </div>
              <button type="submit" className="btn btn-primary">Submit Review</button>
            </form>
          </div>
        )}
        
        <div className="space-y-4">
          {reviews.length > 0 ? (
            reviews.map(review => (
              <div key={review.id} className="border-b border-gray-800 pb-4 last:border-none">
                <div className="flex justify-between mb-2">
                  <div className="font-medium text-gray-200">{review.user.username}</div>
                  <div className="text-sm text-gray-400">{new Date(review.created_at).toLocaleDateString()}</div>
                </div>
                <div className="flex text-yellow-400 mb-2">
                  {Array(5).fill(null).map((_, i) => (
                    <span key={i} className={i < review.rating ? 'text-yellow-400' : 'text-gray-600'}>★</span>
                  ))}
                </div>
                <p className="text-gray-300">{review.comment}</p>
              </div>
            ))
          ) : (
            <div className="text-center py-6 text-gray-400">
              <p>No reviews yet. Be the first to leave a review!</p>
            </div>
          )}
        </div>
      </div>
      
      <div className="card">
        <h2 className="text-xl font-semibold mb-4 text-gray-100">Comments</h2>
        
        {isLoggedIn && (
          <div className="bg-dark-lighter rounded-lg p-4 mb-6">
            <h3 className="text-lg font-medium mb-3 text-gray-200">Ask a Question</h3>
            <form onSubmit={handleCommentSubmit}>
              <div className="form-group">
                <label htmlFor="comment" className="label">Your Question or Comment:</label>
                <textarea 
                  id="comment" 
                  rows="3"
                  value={commentText}
                  onChange={(e) => setCommentText(e.target.value)}
                  required
                  className="input"
                  placeholder="Ask a question about this club..."
                ></textarea>
              </div>
              <button type="submit" className="btn btn-primary">Post Comment</button>
            </form>
          </div>
        )}
        
        <div className="space-y-4">
          {comments.length > 0 ? (
            comments.map(comment => (
              <div key={comment.id} className="border-b border-gray-800 pb-4 last:border-none">
                <div className="flex justify-between mb-2">
                  <div className="font-medium text-gray-200">{comment.user.username}</div>
                  <div className="text-sm text-gray-400">{new Date(comment.created_at).toLocaleDateString()}</div>
                </div>
                <p className="text-gray-300">{comment.content}</p>
              </div>
            ))
          ) : (
            <div className="text-center py-6 text-gray-400">
              <p>No comments yet. Be the first to leave a comment!</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ClubDetails;
