import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { authAPI } from '../utils/api';
import { handleAuthResponse, isAuthenticated, clearAuthData } from '../utils/auth';

const LoginPage = ({ onLoginSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [loginSuccess, setLoginSuccess] = useState(false);
  const navigate = useNavigate();

  // Check if already authenticated and redirect if needed
  useEffect(() => {
    console.log('LoginPage: Checking if already authenticated');
    
    // Clear any error state when page loads
    setError('');
    
    // If already authenticated, redirect to dashboard
    if (isAuthenticated()) {
      console.log('LoginPage: Already authenticated, redirecting to dashboard');
      navigate('/dashboard');
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    
    // Basic form validation
    if (!email || !password) {
      setError('Please enter both email and password');
      setLoading(false);
      return;
    }
    
    console.log(`LoginPage: Attempting login for email: ${email}`);
    
    try {
      // Before attempting login, clear any existing auth data
      // This helps prevent state conflicts
      clearAuthData();
      
      // Use the direct fetch implementation for login
      const data = await authAPI.login(email, password);
      console.log('LoginPage: Login API call successful:', data ? 'Data received' : 'No data');
      
      if (!data) {
        setError('No response from server');
        setLoading(false);
        return;
      }
      
      if (data.access_token && data.user) {
        console.log('LoginPage: Successfully received auth token and user data');
        
        // Store auth data in localStorage using handleAuthResponse utility
        const success = handleAuthResponse(data);
        
        if (success) {
          console.log('LoginPage: Successfully saved authentication data');
          setLoginSuccess(true);
          
          // Call the onLoginSuccess callback if provided
          if (onLoginSuccess) {
            console.log('LoginPage: Calling onLoginSuccess callback');
            onLoginSuccess();
          }
          
          // IMPORTANT: Force a hard page reload instead of using React Router
          // This ensures the authentication state is fully applied
          console.log('LoginPage: Force reloading page to apply authentication state');
          window.location.href = '/dashboard';
        } else {
          setError('Failed to save authentication data');
        }
      } else {
        console.error('LoginPage: Invalid response from server - missing token or user data');
        console.error('Response data:', data);
        setError('Invalid response from server: missing authentication data');
      }
    } catch (error) {
      console.error('LoginPage: Login failed', error);
      
      if (error.status === 401) {
        setError('Invalid email or password');
      } else if (error.message) {
        setError(error.message);
      } else {
        setError('An error occurred during login. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    console.log('LoginPage: Starting Google OAuth login flow');
    
    // Clear any previous errors and show loading state
    setError('');
    setLoading(true);
    
    try {
      // Direct and simple approach - just redirect to the backend Google login endpoint
      authAPI.googleLogin();
    } catch (err) {
      console.error('LoginPage: Failed to redirect to Google login:', err);
      setError('Could not start Google login process');
      setLoading(false);
    }
  };

  // For demo convenience, set admin credentials
  const fillAdminCredentials = () => {
    setEmail('admin@example.com');
    setPassword('admin123!');
  };

  // If login was successful but navigation hasn't happened yet, show a loading state
  if (loginSuccess) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-black p-4">
        <div className="bg-gray-800 rounded-lg p-8 flex flex-col items-center">
          <h2 className="text-xl font-semibold text-white mb-4">Login Successful!</h2>
          <p className="text-gray-300 mb-4">Redirecting to dashboard...</p>
          <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900">
      <div className="max-w-md w-full space-y-8 p-10 bg-gray-800 rounded-xl shadow-lg">
        <div className="text-center">
          <h2 className="mt-6 text-3xl font-bold text-white">Sign in to your account</h2>
          <p className="mt-2 text-sm text-gray-400">
            Or{' '}
            <a href="/register" className="font-medium text-primary hover:text-primary-light">
              create a new account
            </a>
          </p>
        </div>
        
        {error && (
          <div className="bg-red-900 text-white p-3 rounded">
            {error}
          </div>
        )}
        
        {loginSuccess && (
          <div className="bg-green-900 text-white p-3 rounded">
            Login successful! Redirecting...
          </div>
        )}
        
        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <label htmlFor="email-address" className="sr-only">
                Email address
              </label>
              <input
                id="email-address"
                name="email"
                type="email"
                autoComplete="email"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-700 placeholder-gray-500 text-white bg-gray-700 rounded-t-md focus:outline-none focus:ring-primary focus:border-primary focus:z-10 sm:text-sm"
                placeholder="Email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                disabled={loading}
              />
            </div>
            <div>
              <label htmlFor="password" className="sr-only">
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                autoComplete="current-password"
                required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-700 placeholder-gray-500 text-white bg-gray-700 rounded-b-md focus:outline-none focus:ring-primary focus:border-primary focus:z-10 sm:text-sm"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                disabled={loading}
              />
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={loading}
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-primary hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary"
            >
              {loading ? (
                <span className="absolute left-0 inset-y-0 flex items-center pl-3">
                  <svg className="animate-spin h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                </span>
              ) : (
                <span className="absolute left-0 inset-y-0 flex items-center pl-3">
                  <svg className="h-5 w-5 text-primary-dark group-hover:text-primary-light" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                    <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
                  </svg>
                </span>
              )}
              {loading ? 'Signing in...' : 'Sign in'}
            </button>
          </div>

          <div className="text-center">
            <a href="/forgot-password" className="font-medium text-sm text-primary hover:text-primary-light">
              Forgot your password?
            </a>
          </div>
        </form>

        <div className="mt-6 text-center">
          <p className="text-gray-400">
            Don't have an account?{' '}
            <a href="/register" className="text-blue-400 hover:underline">
              Register
            </a>
          </p>
        </div>

        <div className="mt-6 text-center">
          <p className="text-gray-400">
            Or{' '}
            <button
              type="button"
              onClick={handleGoogleLogin}
              className="text-blue-400 hover:underline"
            >
              Sign in with Google
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
