// api.js - Comprehensive API client for FastAPI backend

import { getToken } from './auth';

// Base API URL - empty for relative paths with proxy in development
export const API_BASE = '';
export const API_PATH = '/api';  // Remove 'v1' as it might not be part of the API path

/**
 * Core API request function with automatic token handling
 */
export async function apiRequest(path, { method = 'GET', body, token, headers = {} } = {}) {
  // Get token from auth utility if not provided explicitly
  const authToken = token || getToken();
  
  // Build headers with auth token if available
  const requestHeaders = { 
    'Content-Type': 'application/json',
    ...headers
  };
  
  if (authToken) {
    requestHeaders['Authorization'] = `Bearer ${authToken}`;
  }

  try {
    const response = await fetch(`${API_BASE}${path}`, {
      method,
      headers: requestHeaders,
      body: body ? JSON.stringify(body) : undefined,
      credentials: 'include', // Include cookies for sessions
    });

    // Try to parse JSON, use empty object as fallback
    const data = await response.json().catch(() => ({}));
    
    // Handle API errors
    if (!response.ok) {
      throw {
        status: response.status,
        data,
        message: data.detail || data.message || `Error ${response.status}: ${response.statusText}`
      };
    }
    
    return data;
  } catch (error) {
    // Enhance error with additional info if it's not already our format
    if (!error.status) {
      console.error('API request failed:', error);
      throw {
        status: 0, 
        message: error.message || 'Network error, please check your connection',
        data: {}
      };
    }
    throw error;
  }
}

// Authentication API
export const authAPI = {
  login: (email, password) => 
    apiRequest(`${API_PATH}/auth/login`, {
      method: 'POST',
      body: { email, password }  // Match the UserLogin schema which expects email and password
    }),
    
  register: (userData) => 
    apiRequest(`${API_PATH}/auth/register`, {
      method: 'POST',
      body: userData
    }),
    
  getCurrentUser: () => 
    apiRequest(`${API_PATH}/users/me`, {
      method: 'GET'
    }),
    
  updateProfile: (userData) => 
    apiRequest(`${API_PATH}/users/me`, {
      method: 'PUT',
      body: userData
    })
};

// Clubs API
export const clubsAPI = {
  getAllClubs: (filters = {}) => {
    // Convert filters object to URL params
    const params = new URLSearchParams();
    Object.entries(filters).forEach(([key, value]) => {
      if (value !== undefined && value !== null && value !== '') {
        params.append(key, value);
      }
    });
    
    const queryString = params.toString() ? `?${params.toString()}` : '';
    
    return apiRequest(`${API_PATH}/clubs${queryString}`);
  },
  
  getClubById: (clubId) => 
    apiRequest(`${API_PATH}/clubs/${clubId}`),
    
  createClub: (clubData) => 
    apiRequest(`${API_PATH}/clubs/`, {  // Add trailing slash to be safer
      method: 'POST',
      body: clubData
    }),
    
  updateClub: (clubId, clubData) => 
    apiRequest(`${API_PATH}/clubs/${clubId}`, {
      method: 'PUT',
      body: clubData
    }),
    
  deleteClub: (clubId) => 
    apiRequest(`${API_PATH}/clubs/${clubId}`, {
      method: 'DELETE'
    }),
    
  getClubsByOwner: () => 
    apiRequest(`${API_PATH}/clubs/my-clubs`)
};

// Reservations API
export const reservationsAPI = {
  getMyReservations: () => 
    apiRequest(`${API_PATH}/reservations/my-reservations`),
    
  createReservation: (reservationData) => 
    apiRequest(`${API_PATH}/reservations`, {
      method: 'POST',
      body: reservationData
    }),
    
  cancelReservation: (reservationId) => 
    apiRequest(`${API_PATH}/reservations/${reservationId}/cancel`, {
      method: 'POST'
    }),
    
  getAvailableTimes: (clubId, date) => {
    const params = new URLSearchParams();
    if (date) params.append('date', date);
    
    const queryString = params.toString() ? `?${params.toString()}` : '';
    
    return apiRequest(`${API_PATH}/clubs/${clubId}/available-times${queryString}`);
  }
};

export default {
  apiRequest,
  auth: authAPI,
  clubs: clubsAPI,
  reservations: reservationsAPI
};
