import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { authAPI } from '../utils/api';
import { handleAuthResponse, isAuthenticated } from '../utils/auth';

const OAuthCallback = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const handleCallback = async () => {
      try {
        // Extract the code from the URL
        const urlParams = new URLSearchParams(location.search);
        const code = urlParams.get('code');
        const state = urlParams.get('state');
        
        console.log('OAuth callback parameters:', { code: code ? 'present' : 'missing', state: state ? 'present' : 'missing' });
        
        if (!code) {
          setError('No authorization code provided in the callback URL');
          setLoading(false);
          return;
        }
        
        // Exchange the code for a token using direct fetch to avoid CORS/message channel issues
        try {
          console.log('Starting token exchange with code...');
          const response = await fetch(`/api/v1/auth/google/token-exchange`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ code }),
          });
          
          console.log('Token exchange response status:', response.status);
          
          if (!response.ok) {
            const errorText = await response.text();
            console.error('Token exchange error:', errorText);
            throw new Error(`Failed to exchange token: ${response.status} ${errorText}`);
          }
          
          const data = await response.json();
          console.log('Token exchange successful, storing auth data');
          
          // Store authentication data manually
          localStorage.setItem('userId', data.user.id);
          localStorage.setItem('token', data.access_token);
          localStorage.setItem('username', data.user.username);
          localStorage.setItem('isClubOwner', String(data.user.is_club_owner).toLowerCase());
          localStorage.setItem('role', data.user.role);
          localStorage.setItem('isAuthenticated', 'true');
          
          // Trigger auth change and navigate
          console.log('Triggering auth change event');
          window.dispatchEvent(new Event('auth-change'));
          
          // Navigate after a short delay to allow auth state to update
          setTimeout(() => {
            console.log('Redirecting to dashboard');
            navigate('/dashboard');
          }, 300);
        } catch (fetchError) {
          console.error('Error during token exchange:', fetchError);
          setError(`Authentication failed: ${fetchError.message}`);
          setLoading(false);
        }
      } catch (err) {
        console.error('OAuth callback error:', err);
        setError(err.message || 'Authentication failed');
        setLoading(false);
      }
    };

    // Check if already authenticated
    if (isAuthenticated()) {
      console.log('Already authenticated, redirecting to dashboard');
      navigate('/dashboard');
    } else {
      console.log('Not authenticated, processing OAuth callback');
      handleCallback();
    }
  }, [navigate, location]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-black">
        <div className="text-center p-8 bg-gray-800 rounded-lg shadow-lg">
          <h1 className="text-2xl font-bold text-white mb-4">Completing Authentication</h1>
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
          <p className="text-gray-300 mt-4">Please wait while we complete the authentication process...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-black">
        <div className="text-center p-8 bg-gray-800 rounded-lg shadow-lg max-w-md">
          <h1 className="text-2xl font-bold text-white mb-4">Authentication Failed</h1>
          <div className="bg-red-900/30 border border-red-800 text-red-300 px-4 py-3 rounded mb-4">
            {error}
          </div>
          <button
            onClick={() => navigate('/login')}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded transition-colors"
          >
            Return to Login
          </button>
        </div>
      </div>
    );
  }

  return null;
};

export default OAuthCallback;
