import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const OAuthCallback = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState({});
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const handleCallback = async () => {
      try {
        // Extract the code from the URL
        const urlParams = new URLSearchParams(location.search);
        const code = urlParams.get('code');
        const state = urlParams.get('state');
        
        // Debug info
        const debug = {
          hasCode: Boolean(code),
          hasState: Boolean(state),
          url: window.location.href,
          search: location.search
        };
        
        setDebugInfo(debug);
        console.log('OAuth callback debug info:', debug);
        
        if (!code) {
          console.error('No code parameter found in URL');
          setError('Authentication failed: No authorization code provided');
          setLoading(false);
          return;
        }
        
        console.log('Received OAuth code, redirecting to backend for handling');
        
        // Try direct exchange with the backend
        try {
          // POST to token exchange endpoint
          const tokenResponse = await fetch('/api/v1/auth/google/token-exchange', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ code }),
          });
          
          console.log('Token exchange response status:', tokenResponse.status);
          
          if (tokenResponse.ok) {
            // Handle successful response
            const data = await tokenResponse.json();
            console.log('Received auth data:', { 
              userId: data.user.id,
              username: data.user.username,
              hasToken: Boolean(data.access_token)
            });
            
            // Store authentication data
            localStorage.setItem('userId', data.user.id);
            localStorage.setItem('token', data.access_token);
            localStorage.setItem('username', data.user.username);
            localStorage.setItem('isClubOwner', String(data.user.is_club_owner).toLowerCase());
            localStorage.setItem('role', data.user.role);
            localStorage.setItem('isAuthenticated', 'true');
            
            // Dispatch auth change event
            window.dispatchEvent(new Event('auth-change'));
            
            // Navigate to dashboard
            setTimeout(() => {
              navigate('/dashboard');
            }, 300);
          } else {
            // Handle error response
            const responseText = await tokenResponse.text();
            console.error('Token exchange failed:', responseText);
            setError(`Authentication failed: ${tokenResponse.status} - ${responseText}`);
            setLoading(false);
          }
        } catch (tokenError) {
          console.error('Token exchange error:', tokenError);
          
          // Fall back to the old method - direct redirect to backend callback
          window.location.href = `/api/v1/auth/google/callback?code=${encodeURIComponent(code)}`;
        }
      } catch (err) {
        console.error('OAuth callback general error:', err);
        setError(err.message || 'Authentication failed');
        setLoading(false);
      }
    };

    handleCallback();
  }, [navigate, location]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-black">
        <div className="text-center p-8 bg-gray-800 rounded-lg shadow-lg">
          <h1 className="text-2xl font-bold text-white mb-4">Completing Authentication</h1>
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
          <p className="text-gray-300 mt-4">Please wait while we complete the authentication process...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-black">
        <div className="text-center p-8 bg-gray-800 rounded-lg shadow-lg max-w-md">
          <h1 className="text-2xl font-bold text-white mb-4">Authentication Failed</h1>
          <div className="bg-red-900/30 border border-red-800 text-red-300 px-4 py-3 rounded mb-4">
            {error}
          </div>
          {Object.keys(debugInfo).length > 0 && (
            <div className="bg-gray-700 text-gray-300 text-left p-3 rounded mb-4 text-xs overflow-auto">
              <pre>{JSON.stringify(debugInfo, null, 2)}</pre>
            </div>
          )}
          <button
            onClick={() => navigate('/login')}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded transition-colors"
          >
            Return to Login
          </button>
        </div>
      </div>
    );
  }

  return null;
};

export default OAuthCallback;
