import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { authAPI, clubsAPI, reservationsAPI } from '../utils/api';
import { isAuthenticated, getUserData, logout } from '../utils/auth';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [clubs, setClubs] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect if not authenticated
    if (!isAuthenticated()) {
      navigate('/login');
      return;
    }

    // Try to get cached user data first
    const cachedUser = getUserData();
    if (cachedUser) {
      setUser(cachedUser);
    }

    // Fetch fresh data
    fetchUserData();
  }, [navigate]);

  const fetchUserData = async () => {
    setLoading(true);
    setError(null);
    
    try {
      // Fetch user data
      const userData = await authAPI.getCurrentUser();
      setUser(userData);
      
      // Fetch user's clubs (if any)
      try {
        const clubsData = await clubsAPI.getClubsByOwner();
        setClubs(clubsData || []);
      } catch (clubErr) {
        console.error('Error fetching clubs:', clubErr);
        setClubs([]);
      }
      
      // Fetch user's reservations (if any)
      try {
        const reservationsData = await reservationsAPI.getMyReservations();
        setReservations(reservationsData || []);
      } catch (resErr) {
        console.error('Error fetching reservations:', resErr);
        setReservations([]);
      }
      
      setLoading(false);
    } catch (err) {
      console.error('Error fetching user data:', err);
      setError('Failed to load your profile. Please try again.');
      setLoading(false);
      
      // If unauthorized, redirect to login
      if (err.status === 401) {
        logout();
      }
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleCreateClub = () => {
    navigate('/create-club');
  };

  if (loading && !user) {
    return (
      <div className="min-h-screen bg-black flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-400">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (error && !user) {
    return (
      <div className="min-h-screen bg-black p-6">
        <div className="max-w-4xl mx-auto bg-red-900/30 border border-red-800 text-red-300 p-4 rounded">
          <p>{error}</p>
          <button 
            onClick={() => fetchUserData()} 
            className="mt-4 bg-red-800 hover:bg-red-700 px-4 py-2 rounded text-sm"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black text-white p-6">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold mb-8">Dashboard</h1>
        
        {/* User Info */}
        <div className="bg-gray-800 rounded-lg p-6 mb-8 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Your Profile</h2>
          <div className="flex flex-col md:flex-row items-center md:items-start gap-6">
            <div className="w-24 h-24 bg-gray-700 rounded-full flex items-center justify-center text-3xl font-bold">
              {user?.username?.charAt(0).toUpperCase() || 'U'}
            </div>
            <div className="flex-1">
              <p className="text-lg font-medium">{user?.username || 'User'}</p>
              <p className="text-gray-400">{user?.email || 'user@example.com'}</p>
              {user?.first_name && user?.last_name && (
                <p className="text-gray-300">{user.first_name} {user.last_name}</p>
              )}
              <div className="mt-4 flex gap-2">
                <button 
                  className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded text-sm"
                  onClick={() => navigate('/profile/edit')}
                >
                  Edit Profile
                </button>
                <button 
                  className="bg-gray-700 hover:bg-gray-600 px-4 py-2 rounded text-sm"
                  onClick={handleLogout}
                >
                  Logout
                </button>
              </div>
            </div>
          </div>
        </div>
        
        {/* Your Clubs */}
        <div className="bg-gray-800 rounded-lg p-6 mb-8 shadow-lg">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold">Your Clubs</h2>
            {user?.is_club_owner && (
              <button 
                onClick={handleCreateClub}
                className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded text-sm"
              >
                Create Club
              </button>
            )}
          </div>
          
          {clubs.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-400 mb-4">
                {user?.is_club_owner 
                  ? "You haven't created any clubs yet." 
                  : "You haven't joined any clubs yet."}
              </p>
              {user?.is_club_owner ? (
                <button 
                  onClick={handleCreateClub}
                  className="bg-blue-600 hover:bg-blue-700 px-6 py-2 rounded"
                >
                  Create Your First Club
                </button>
              ) : (
                <button 
                  onClick={() => navigate('/')}
                  className="bg-blue-600 hover:bg-blue-700 px-6 py-2 rounded"
                >
                  Find Clubs
                </button>
              )}
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full text-left">
                <thead className="bg-gray-700 text-gray-300">
                  <tr>
                    <th className="p-3 rounded-tl-lg">Club Name</th>
                    <th className="p-3">Location</th>
                    <th className="p-3">Your Role</th>
                    <th className="p-3 rounded-tr-lg">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {clubs.map((club) => (
                    <tr key={club.id} className="border-b border-gray-700">
                      <td className="p-3 font-medium">{club.name}</td>
                      <td className="p-3">{club.location || 'N/A'}</td>
                      <td className="p-3">{club.user_is_owner ? 'Owner' : 'Member'}</td>
                      <td className="p-3 flex gap-2">
                        <a href={`/clubs/${club.id}`} className="text-blue-400 hover:underline">
                          View
                        </a>
                        {club.user_is_owner && (
                          <a href={`/clubs/${club.id}/edit`} className="text-blue-400 hover:underline ml-3">
                            Edit
                          </a>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
        
        {/* Upcoming Reservations */}
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Upcoming Reservations</h2>
          
          {reservations.length === 0 ? (
            <div>
              <p className="text-gray-400 mb-4">You don't have any upcoming reservations.</p>
              <button 
                onClick={() => navigate('/')}
                className="bg-orange-600 hover:bg-orange-700 px-4 py-2 rounded text-sm"
              >
                Find Courts to Book
              </button>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full text-left">
                <thead className="bg-gray-700 text-gray-300">
                  <tr>
                    <th className="p-3 rounded-tl-lg">Club</th>
                    <th className="p-3">Date</th>
                    <th className="p-3">Time</th>
                    <th className="p-3">Status</th>
                    <th className="p-3 rounded-tr-lg">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {reservations.map((reservation) => (
                    <tr key={reservation.id} className="border-b border-gray-700">
                      <td className="p-3 font-medium">{reservation.club_name}</td>
                      <td className="p-3">{new Date(reservation.date).toLocaleDateString()}</td>
                      <td className="p-3">{reservation.start_time} - {reservation.end_time}</td>
                      <td className="p-3">
                        <span 
                          className={`px-2 py-1 rounded text-xs ${
                            reservation.status === 'confirmed' ? 'bg-green-900/50 text-green-300' : 
                            reservation.status === 'pending' ? 'bg-yellow-900/50 text-yellow-300' : 
                            'bg-red-900/50 text-red-300'
                          }`}
                        >
                          {reservation.status.charAt(0).toUpperCase() + reservation.status.slice(1)}
                        </span>
                      </td>
                      <td className="p-3">
                        {reservation.status !== 'cancelled' && (
                          <button 
                            onClick={() => navigate(`/reservations/${reservation.id}/cancel`)}
                            className="text-red-400 hover:text-red-300 text-sm"
                          >
                            Cancel
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
