import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { clubsAPI } from '../utils/api';
import { isAuthenticated, getUserData, getToken } from '../utils/auth';

function CreateClub() {
  const [formData, setFormData] = useState({
    name: '',
    town: '',
    telephone: '',
    hourly_price: '',
    description: '',
    address: '',
    website: '',
    social_media: {}
  });
  const [pictures, setPictures] = useState([]);
  const [pictureUrls, setPictureUrls] = useState([]);
  const [uploadStatus, setUploadStatus] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect to login if not logged in
    if (!isAuthenticated()) {
      navigate('/login');
    }
    
    // Check if user is a club owner
    const userData = getUserData();
    if (userData && !userData.is_club_owner) {
      navigate('/dashboard');
    }
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSocialMediaChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      social_media: {
        ...formData.social_media,
        [name]: value
      }
    });
  };

  const handlePictureChange = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      // Limit number of files to 5
      const selectedFiles = Array.from(e.target.files).slice(0, 5);
      setPictures(selectedFiles);
      
      // Create preview URLs
      const urls = selectedFiles.map(file => URL.createObjectURL(file));
      setPictureUrls(urls);
    }
  };

  const uploadPictures = async (clubId, ownerId) => {
    if (pictures.length === 0) return [];
    
    const uploadedUrls = [];
    setUploadStatus('Uploading pictures...');
    
    try {
      for (const picture of pictures) {
        const formData = new FormData();
        formData.append('file', picture);
        
        const response = await fetch(`/api/v1/clubs/${clubId}/upload?owner_id=${ownerId}`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${getToken()}`
          },
          body: formData
        });
        
        if (!response.ok) {
          console.error('Failed to upload picture:', await response.text());
          continue;
        }
        
        const data = await response.json();
        if (data.pictures && data.pictures.length > 0) {
          uploadedUrls.push(...data.pictures);
        }
      }
      
      setUploadStatus('Pictures uploaded successfully!');
      return uploadedUrls;
    } catch (err) {
      console.error('Error uploading pictures:', err);
      setUploadStatus('Failed to upload some pictures. The club was created but you may need to add pictures later.');
      return uploadedUrls;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);
    
    // Validate hourly_price is a number
    const hourlyPrice = parseFloat(formData.hourly_price);
    if (isNaN(hourlyPrice)) {
      setError('Hourly price must be a valid number');
      setLoading(false);
      return;
    }
    
    // Create a clean version of the data with correct types
    const clubData = {
      ...formData,
      hourly_price: hourlyPrice
    };
    
    console.log("Submitting club data:", clubData);
    
    try {
      // Try the preferred API method first
      const token = getToken();
      
      if (!token) {
        setError('Authentication token not found. Please log in again.');
        setLoading(false);
        return;
      }
      
      // Use direct fetch to better control the process
      const response = await fetch('/api/v1/clubs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(clubData),
        credentials: 'include'
      });
      
      console.log(`Response status: ${response.status} ${response.statusText}`);
      
      // Try to get response data
      let club;
      try {
        club = await response.json();
        console.log("Club created successfully:", club);
      } catch (parseError) {
        console.error("Failed to parse response:", parseError);
        throw new Error("Failed to parse server response");
      }
      
      if (!response.ok) {
        throw {
          status: response.status,
          data: club,
          message: club.detail || `Error ${response.status}: ${response.statusText}`
        };
      }
      
      // Now upload pictures if any
      if (pictures.length > 0 && club.id) {
        await uploadPictures(club.id, club.owner_id);
      }
      
      // Success! Navigate to dashboard
      setLoading(false);
      navigate('/dashboard');
    } catch (err) {
      console.error('Error creating club:', err);
      setError(err.message || 'An error occurred while creating the club. Please check your connection and try again.');
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black p-6">
      <div className="max-w-4xl mx-auto bg-gray-800 rounded-lg shadow-lg p-8">
        <h2 className="text-2xl font-bold text-white mb-6">Create Your Club</h2>
        
        {error && (
          <div className="bg-red-900/30 border border-red-800 text-red-300 px-4 py-3 rounded mb-6">
            {error}
          </div>
        )}
        
        {uploadStatus && (
          <div className="bg-blue-900/30 border border-blue-800 text-blue-300 px-4 py-3 rounded mb-6">
            {uploadStatus}
          </div>
        )}
      
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            <div>
              <label htmlFor="name" className="block text-gray-300 mb-2">Club Name*</label>
              <input 
                type="text" 
                id="name" 
                name="name" 
                value={formData.name}
                onChange={handleChange}
                required 
                disabled={loading}
                className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
              />
            </div>
            
            <div>
              <label htmlFor="town" className="block text-gray-300 mb-2">Town/City*</label>
              <input 
                type="text" 
                id="town" 
                name="town" 
                value={formData.town}
                onChange={handleChange}
                required 
                disabled={loading}
                className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
              />
            </div>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            <div>
              <label htmlFor="telephone" className="block text-gray-300 mb-2">Telephone Number*</label>
              <input 
                type="tel" 
                id="telephone" 
                name="telephone" 
                value={formData.telephone}
                onChange={handleChange}
                required 
                disabled={loading}
                className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
              />
            </div>
            
            <div>
              <label htmlFor="hourly_price" className="block text-gray-300 mb-2">Hourly Price*</label>
              <input 
                type="number" 
                id="hourly_price" 
                name="hourly_price" 
                min="0" 
                step="0.01"
                value={formData.hourly_price}
                onChange={handleChange}
                required 
                disabled={loading}
                className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
              />
            </div>
          </div>
          
          <div className="mb-6">
            <label htmlFor="description" className="block text-gray-300 mb-2">Description</label>
            <textarea 
              id="description" 
              name="description" 
              rows="5"
              value={formData.description}
              onChange={handleChange}
              placeholder="Describe your club, facilities, and any unique features"
              disabled={loading}
              className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
            ></textarea>
          </div>
          
          <div className="mb-6">
            <label htmlFor="address" className="block text-gray-300 mb-2">Address</label>
            <input 
              type="text" 
              id="address" 
              name="address" 
              value={formData.address}
              onChange={handleChange}
              placeholder="Street address"
              disabled={loading}
              className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
            />
          </div>
          
          <div className="mb-6">
            <label htmlFor="website" className="block text-gray-300 mb-2">Website</label>
            <input 
              type="url" 
              id="website" 
              name="website" 
              value={formData.website}
              onChange={handleChange}
              placeholder="https://example.com"
              disabled={loading}
              className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
            />
          </div>
          
          <div className="mb-6">
            <label htmlFor="pictures" className="block text-gray-300 mb-2">Club Pictures (Max 5)</label>
            <input
              type="file"
              id="pictures"
              name="pictures"
              accept="image/jpeg,image/png,image/gif,image/webp"
              multiple
              onChange={handlePictureChange}
              disabled={loading}
              className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
            />
            {pictureUrls.length > 0 && (
              <div className="mt-3 flex flex-wrap gap-2">
                {pictureUrls.map((url, index) => (
                  <div key={index} className="w-20 h-20 relative">
                    <img 
                      src={url} 
                      alt={`Preview ${index + 1}`} 
                      className="w-20 h-20 object-cover rounded"
                    />
                  </div>
                ))}
              </div>
            )}
            <p className="mt-2 text-sm text-gray-400">
              Upload images of your club to attract more customers. Supported formats: JPEG, PNG, GIF, WebP
            </p>
          </div>
          
          <div className="mb-8">
            <h3 className="text-white font-medium mb-3">Social Media</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="facebook" className="block text-gray-300 mb-2">Facebook</label>
                <input 
                  type="text" 
                  id="facebook" 
                  name="facebook" 
                  value={formData.social_media.facebook || ''}
                  onChange={handleSocialMediaChange}
                  placeholder="Facebook page URL"
                  disabled={loading}
                  className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
                />
              </div>
              
              <div>
                <label htmlFor="instagram" className="block text-gray-300 mb-2">Instagram</label>
                <input 
                  type="text" 
                  id="instagram" 
                  name="instagram" 
                  value={formData.social_media.instagram || ''}
                  onChange={handleSocialMediaChange}
                  placeholder="Instagram handle"
                  disabled={loading}
                  className="w-full p-3 bg-gray-700 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
                />
              </div>
            </div>
          </div>
          
          <div className="flex justify-between">
            <button 
              type="button" 
              onClick={() => navigate('/dashboard')}
              disabled={loading}
              className="bg-gray-700 hover:bg-gray-600 text-white font-bold py-3 px-6 rounded transition-colors"
            >
              Cancel
            </button>
            
            <button 
              type="submit" 
              disabled={loading}
              className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded transition-colors"
            >
              {loading ? 'Creating...' : 'Create Club'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default CreateClub;
