import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { isAuthenticated, getUserData } from '../utils/auth';
import { clubsAPI, reviewsAPI } from '../utils/api';

function ClubDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [club, setClub] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeImageIndex, setActiveImageIndex] = useState(0);
  
  // User state
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(null);
  const [isMember, setIsMember] = useState(false);
  const [isOwner, setIsOwner] = useState(false);
  const [membershipLoading, setMembershipLoading] = useState(false);
  
  // Review/comment state
  const [reviewText, setReviewText] = useState('');
  const [rating, setRating] = useState(5);
  const [commentText, setCommentText] = useState('');
  const [reviews, setReviews] = useState([]);
  const [comments, setComments] = useState([]);
  const [reviewSubmitting, setReviewSubmitting] = useState(false);
  const [commentSubmitting, setCommentSubmitting] = useState(false);

  useEffect(() => {
    // Check if user is logged in
    const userData = getUserData();
    if (userData) {
      setIsLoggedIn(true);
      setUserId(userData.id);
    }
    
    fetchClubDetails();
  }, [id]);

  const fetchClubDetails = async () => {
    try {
      setLoading(true);
      // Get club details
      const clubData = await clubsAPI.getClubById(id);
      setClub(clubData);
      
      if (isLoggedIn) {
        // Check if current user is the owner
        setIsOwner(clubData.owner_id === parseInt(userId));
        
        // Check if current user is a member
        // In a real implementation, you'd have an API endpoint to check this
        // For now we'll use a placeholder
        setIsMember(false);
      }
      
      // Fetch reviews
      try {
        const reviewsData = await reviewsAPI.getClubReviews(id);
        setReviews(reviewsData);
      } catch (reviewErr) {
        console.error("Error fetching reviews:", reviewErr);
        setReviews([]);
      }
      
      // Fetch comments
      try {
        const commentsData = await reviewsAPI.getClubComments(id);
        setComments(commentsData);
      } catch (commentErr) {
        console.error("Error fetching comments:", commentErr);
        setComments([]);
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
      console.error('Error fetching club details:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    if (!isLoggedIn) {
      navigate('/login');
      return;
    }
    
    setReviewSubmitting(true);
    try {
      const reviewData = {
        club_id: parseInt(id),
        rating: rating,
        comment: reviewText
      };
      
      await reviewsAPI.createReview(reviewData);
      
      // Refetch reviews to show the new one
      const reviewsData = await reviewsAPI.getClubReviews(id);
      setReviews(reviewsData);
      
      // Reset form
      setReviewText('');
      setRating(5);
      
    } catch (err) {
      console.error("Error submitting review:", err);
      alert("Failed to submit review. Please try again.");
    } finally {
      setReviewSubmitting(false);
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!isLoggedIn) {
      navigate('/login');
      return;
    }
    
    setCommentSubmitting(true);
    try {
      const commentData = {
        club_id: parseInt(id),
        content: commentText,
        parent_id: null // For a new top-level comment
      };
      
      await reviewsAPI.createComment(commentData);
      
      // Refetch comments to show the new one
      const commentsData = await reviewsAPI.getClubComments(id);
      setComments(commentsData);
      
      // Reset form
      setCommentText('');
      
    } catch (err) {
      console.error("Error submitting comment:", err);
      alert("Failed to submit comment. Please try again.");
    } finally {
      setCommentSubmitting(false);
    }
  };
  
  const handleJoinClub = async () => {
    if (!isLoggedIn) {
      navigate('/login');
      return;
    }
    
    setMembershipLoading(true);
    try {
      await clubsAPI.joinClub(id);
      setIsMember(true);
      alert("You have successfully joined this club!");
    } catch (err) {
      console.error("Error joining club:", err);
      alert("Failed to join club. Please try again.");
    } finally {
      setMembershipLoading(false);
    }
  };
  
  const handleLeaveClub = async () => {
    if (!isLoggedIn) {
      navigate('/login');
      return;
    }
    
    setMembershipLoading(true);
    try {
      await clubsAPI.leaveClub(id);
      setIsMember(false);
      alert("You have left this club.");
    } catch (err) {
      console.error("Error leaving club:", err);
      alert("Failed to leave club. Please try again.");
    } finally {
      setMembershipLoading(false);
    }
  };

  const nextImage = () => {
    if (club?.pictures?.length > 0) {
      setActiveImageIndex((prevIndex) => 
        prevIndex === club.pictures.length - 1 ? 0 : prevIndex + 1
      );
    }
  };

  const prevImage = () => {
    if (club?.pictures?.length > 0) {
      setActiveImageIndex((prevIndex) => 
        prevIndex === 0 ? club.pictures.length - 1 : prevIndex - 1
      );
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-black p-6">
        <div className="max-w-4xl mx-auto bg-gray-800 rounded-lg shadow-lg p-8 text-white text-center">
          <p>Loading club details...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-black p-6">
        <div className="max-w-4xl mx-auto bg-gray-800 rounded-lg shadow-lg p-8 text-white text-center">
          <p className="text-red-400">{error}</p>
          <button 
            onClick={() => navigate('/')}
            className="mt-4 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
          >
            Return to Home
          </button>
        </div>
      </div>
    );
  }

  if (!club) {
    return (
      <div className="min-h-screen bg-black p-6">
        <div className="max-w-4xl mx-auto bg-gray-800 rounded-lg shadow-lg p-8 text-white text-center">
          <p>Club not found</p>
          <button 
            onClick={() => navigate('/')}
            className="mt-4 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
          >
            Return to Home
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black p-6">
      <div className="max-w-5xl mx-auto bg-gray-800 rounded-lg shadow-lg p-8">
        {/* Club Header */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-white">{club.name}</h1>
          
          <div className="flex space-x-3">
            {isLoggedIn && isOwner && (
              <button 
                onClick={() => navigate(`/clubs/${id}/edit`)}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
              >
                Edit Club
              </button>
            )}
            
            {isLoggedIn && !isOwner && (
              isMember ? (
                <button 
                  onClick={handleLeaveClub}
                  disabled={membershipLoading}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                >
                  {membershipLoading ? 'Processing...' : 'Leave Club'}
                </button>
              ) : (
                <button 
                  onClick={handleJoinClub}
                  disabled={membershipLoading}
                  className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
                >
                  {membershipLoading ? 'Processing...' : 'Join Club'}
                </button>
              )
            )}
          </div>
        </div>
        
        {/* Club Gallery */}
        <div className="mb-8">
          {club.pictures && club.pictures.length > 0 ? (
            <div className="relative">
              <img 
                src={club.pictures[activeImageIndex]} 
                alt={`${club.name} - Image ${activeImageIndex + 1}`}
                className="w-full h-96 object-cover rounded-lg" 
              />
              
              {club.pictures.length > 1 && (
                <div className="absolute inset-0 flex items-center justify-between px-4">
                  <button 
                    onClick={prevImage} 
                    className="bg-black/50 hover:bg-black/70 text-white w-10 h-10 rounded-full flex items-center justify-center"
                  >
                    &lt;
                  </button>
                  <button 
                    onClick={nextImage} 
                    className="bg-black/50 hover:bg-black/70 text-white w-10 h-10 rounded-full flex items-center justify-center"
                  >
                    &gt;
                  </button>
                </div>
              )}
              
              {club.pictures.length > 1 && (
                <div className="flex justify-center mt-3 space-x-2">
                  {club.pictures.map((pic, index) => (
                    <button 
                      key={index}
                      onClick={() => setActiveImageIndex(index)}
                      className={`w-3 h-3 rounded-full ${
                        index === activeImageIndex ? 'bg-blue-500' : 'bg-gray-500'
                      }`}
                    ></button>
                  ))}
                </div>
              )}
            </div>
          ) : (
            <div className="w-full h-96 bg-gray-700 rounded-lg flex items-center justify-center">
              <p className="text-gray-400">No images available</p>
            </div>
          )}
        </div>
        
        {/* Club Info */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-8">
          <div className="col-span-2">
            <div className="bg-gray-700 rounded-lg p-6 mb-6">
              <h2 className="text-xl font-semibold text-white mb-4">About this Club</h2>
              <p className="text-gray-300 mb-4">
                {club.description || 'No description provided.'}
              </p>
              
              {(club.website || (club.social_media && Object.keys(club.social_media).length > 0)) && (
                <div className="mt-4">
                  <h3 className="text-lg font-medium text-white mb-2">Links</h3>
                  <div className="flex flex-wrap gap-3">
                    {club.website && (
                      <a 
                        href={club.website} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="bg-gray-600 hover:bg-gray-500 px-3 py-1 rounded text-sm"
                      >
                        Website
                      </a>
                    )}
                    {club.social_media?.facebook && (
                      <a 
                        href={club.social_media.facebook} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="bg-blue-900 hover:bg-blue-800 px-3 py-1 rounded text-sm"
                      >
                        Facebook
                      </a>
                    )}
                    {club.social_media?.instagram && (
                      <a 
                        href={`https://instagram.com/${club.social_media.instagram}`} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="bg-purple-900 hover:bg-purple-800 px-3 py-1 rounded text-sm"
                      >
                        Instagram
                      </a>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>
          
          <div>
            <div className="bg-gray-700 rounded-lg p-6">
              <h2 className="text-xl font-semibold text-white mb-4">Club Information</h2>
              
              <div className="space-y-3">
                <div>
                  <p className="text-gray-400">Location</p>
                  <p className="text-white">{club.town}</p>
                </div>
                
                {club.address && (
                  <div>
                    <p className="text-gray-400">Address</p>
                    <p className="text-white">{club.address}</p>
                  </div>
                )}
                
                <div>
                  <p className="text-gray-400">Contact</p>
                  <p className="text-white">{club.telephone}</p>
                </div>
                
                <div>
                  <p className="text-gray-400">Price</p>
                  <p className="text-white">${club.hourly_price}/hour</p>
                </div>
                
                {club.owner_name && (
                  <div>
                    <p className="text-gray-400">Owner</p>
                    <p className="text-white">{club.owner_name}</p>
                  </div>
                )}
                
                <div>
                  <p className="text-gray-400">Rating</p>
                  <div className="flex items-center">
                    <div className="flex">
                      {Array(5).fill(null).map((_, i) => (
                        <span 
                          key={i} 
                          className={`text-xl ${i < Math.round(club.average_rating || 0) ? 'text-yellow-400' : 'text-gray-500'}`}
                        >
                          ★
                        </span>
                      ))}
                    </div>
                    <span className="ml-2 text-white">
                      {club.average_rating ? club.average_rating.toFixed(1) : 'No ratings'}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        
        {/* Reviews Section */}
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-semibold text-white">Reviews</h2>
            <span className="text-gray-400">
              {club.reviews_count || 0} {club.reviews_count === 1 ? 'review' : 'reviews'}
            </span>
          </div>
          
          {isLoggedIn && !isOwner && (
            <div className="bg-gray-700 rounded-lg p-6 mb-6">
              <h3 className="text-lg font-medium text-white mb-4">Write a Review</h3>
              <form onSubmit={handleReviewSubmit}>
                <div className="mb-4">
                  <label className="block text-gray-300 mb-2">Your Rating</label>
                  <div className="flex">
                    {[5, 4, 3, 2, 1].map((star) => (
                      <label key={star} className="cursor-pointer px-1">
                        <input 
                          type="radio" 
                          name="rating" 
                          value={star} 
                          checked={rating === star}
                          onChange={() => setRating(star)}
                          className="sr-only"
                        />
                        <span 
                          className={`text-2xl ${rating >= star ? 'text-yellow-400' : 'text-gray-500'}`}
                        >
                          ★
                        </span>
                      </label>
                    ))}
                  </div>
                </div>
                
                <div className="mb-4">
                  <label htmlFor="review" className="block text-gray-300 mb-2">Your Review</label>
                  <textarea 
                    id="review" 
                    rows="4"
                    value={reviewText}
                    onChange={(e) => setReviewText(e.target.value)}
                    required
                    disabled={reviewSubmitting}
                    className="w-full p-3 bg-gray-800 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
                    placeholder="Share your experience with this club..."
                  ></textarea>
                </div>
                
                <button 
                  type="submit"
                  disabled={reviewSubmitting}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
                >
                  {reviewSubmitting ? 'Submitting...' : 'Submit Review'}
                </button>
              </form>
            </div>
          )}
          
          {reviews.length > 0 ? (
            <div className="space-y-4">
              {reviews.map(review => (
                <div key={review.id} className="bg-gray-700 rounded-lg p-6">
                  <div className="flex justify-between items-center mb-2">
                    <div className="flex items-center">
                      <span className="font-medium text-white">{review.user.username}</span>
                      <span className="text-gray-400 text-sm ml-3">
                        {new Date(review.created_at).toLocaleDateString()}
                      </span>
                    </div>
                    <div className="flex">
                      {Array(5).fill(null).map((_, i) => (
                        <span 
                          key={i} 
                          className={`text-lg ${i < review.rating ? 'text-yellow-400' : 'text-gray-500'}`}
                        >
                          ★
                        </span>
                      ))}
                    </div>
                  </div>
                  <p className="text-gray-300">{review.comment}</p>
                </div>
              ))}
            </div>
          ) : (
            <div className="bg-gray-700 rounded-lg p-6 text-center">
              <p className="text-gray-400">No reviews yet. Be the first to leave a review!</p>
            </div>
          )}
        </div>
        
        {/* Comments Section */}
        <div>
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-semibold text-white">Comments</h2>
            <span className="text-gray-400">
              {comments.length || 0} {comments.length === 1 ? 'comment' : 'comments'}
            </span>
          </div>
          
          {isLoggedIn && (
            <div className="bg-gray-700 rounded-lg p-6 mb-6">
              <h3 className="text-lg font-medium text-white mb-4">Leave a Comment</h3>
              <form onSubmit={handleCommentSubmit}>
                <div className="mb-4">
                  <label htmlFor="comment" className="block text-gray-300 mb-2">Your Comment</label>
                  <textarea 
                    id="comment" 
                    rows="3"
                    value={commentText}
                    onChange={(e) => setCommentText(e.target.value)}
                    required
                    disabled={commentSubmitting}
                    className="w-full p-3 bg-gray-800 text-white rounded border border-gray-600 focus:border-blue-500 focus:outline-none"
                    placeholder="Ask a question or share information..."
                  ></textarea>
                </div>
                
                <button 
                  type="submit"
                  disabled={commentSubmitting}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
                >
                  {commentSubmitting ? 'Submitting...' : 'Post Comment'}
                </button>
              </form>
            </div>
          )}
          
          {comments.length > 0 ? (
            <div className="space-y-4">
              {comments.map(comment => (
                <div key={comment.id} className="bg-gray-700 rounded-lg p-6">
                  <div className="flex items-center mb-2">
                    <span className="font-medium text-white">{comment.user.username}</span>
                    <span className="text-gray-400 text-sm ml-3">
                      {new Date(comment.created_at).toLocaleDateString()}
                    </span>
                  </div>
                  <p className="text-gray-300">{comment.content}</p>
                </div>
              ))}
            </div>
          ) : (
            <div className="bg-gray-700 rounded-lg p-6 text-center">
              <p className="text-gray-400">No comments yet. Be the first to leave a comment!</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ClubDetails;
