import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';

const ReservationModal = ({ club, isOpen, onClose, isLoggedIn, userData }) => {
  const [date, setDate] = useState(format(new Date(), 'yyyy-MM-dd'));
  const [timeSlots, setTimeSlots] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [duration, setDuration] = useState(1);
  const [paymentMethod, setPaymentMethod] = useState(isLoggedIn ? "cash" : "card");
  const [guestName, setGuestName] = useState("");
  const [estimatedPrice, setEstimatedPrice] = useState(0);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  
  // Calculate estimated price whenever duration or club changes
  useEffect(() => {
    if (club && duration) {
      setEstimatedPrice(parseFloat(club.hourly_price) * parseFloat(duration));
    }
  }, [club, duration]);
  
  // Fetch available time slots when date changes
  useEffect(() => {
    if (club && isOpen) {
      fetchAvailableTimeSlots();
    }
  }, [date, club, isOpen]);
  
  const fetchAvailableTimeSlots = async () => {
    setLoading(true);
    setError("");
    
    try {
      const response = await fetch(`/api/v1/reservations/available-slots/${club.id}?date=${date}`);
      
      if (response.ok) {
        const data = await response.json();
        setTimeSlots(data);
      } else {
        const errorData = await response.json();
        setError(errorData.detail || "Failed to fetch available time slots");
      }
    } catch (error) {
      console.error("Error fetching time slots:", error);
      setError("An error occurred while fetching available time slots");
    } finally {
      setLoading(false);
    }
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    
    // Validation
    if (!selectedTimeSlot) {
      setError("Please select a time slot");
      return;
    }
    
    if (!isLoggedIn && !guestName) {
      setError("Please enter your name for the reservation");
      return;
    }
    
    if (!isLoggedIn && paymentMethod === "cash") {
      setError("Non-logged in users can only pay by card");
      return;
    }
    
    // Prepare reservation data
    const [hours, minutes] = selectedTimeSlot.start_time.split(':').map(Number);
    const reservationTime = new Date(date);
    reservationTime.setHours(hours, minutes, 0, 0);
    
    const reservationData = {
      club_id: club.id,
      reservation_time: reservationTime.toISOString(),
      duration: parseFloat(duration),
      payment_method: paymentMethod
    };
    
    // Add guest name for non-logged in users
    if (!isLoggedIn) {
      reservationData.guest_name = guestName;
    }
    
    try {
      const response = await fetch('/api/v1/reservations/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(reservationData),
      });
      
      if (response.ok) {
        const data = await response.json();
        setSuccess("Reservation created successfully!");
        
        // Reset form
        setSelectedTimeSlot(null);
        setDuration(1);
        setPaymentMethod(isLoggedIn ? "cash" : "card");
        setGuestName("");
        
        // Refresh available time slots
        setTimeout(() => {
          fetchAvailableTimeSlots();
        }, 1000);
      } else {
        const errorData = await response.json();
        setError(errorData.detail || "Failed to create reservation");
      }
    } catch (error) {
      console.error("Error creating reservation:", error);
      setError("An error occurred while creating the reservation");
    }
  };
  
  if (!isOpen) return null;
  
  return (
    <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
      <div className="bg-gray-900 rounded-lg p-6 w-full max-w-md max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold text-gray-100">Reserve {club.name}</h2>
          <button 
            onClick={onClose}
            className="text-gray-400 hover:text-white"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        
        {error && (
          <div className="bg-red-900 text-white p-3 rounded mb-4">
            {error}
          </div>
        )}
        
        {success && (
          <div className="bg-green-900 text-white p-3 rounded mb-4">
            {success}
          </div>
        )}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group mb-4">
            <label htmlFor="reservation-date" className="block text-sm font-medium text-gray-300 mb-1">
              Date
            </label>
            <input
              id="reservation-date"
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              min={format(new Date(), 'yyyy-MM-dd')}
              className="input"
              required
            />
          </div>
          
          <div className="form-group mb-4">
            <label className="block text-sm font-medium text-gray-300 mb-1">
              Available Time Slots
            </label>
            {loading ? (
              <div className="flex justify-center py-2">
                <div className="animate-spin rounded-full h-6 w-6 border-t-2 border-b-2 border-primary"></div>
              </div>
            ) : timeSlots.length > 0 ? (
              <div className="grid grid-cols-3 gap-2">
                {timeSlots.map((slot, index) => (
                  <button
                    key={index}
                    type="button"
                    disabled={!slot.is_available}
                    onClick={() => setSelectedTimeSlot(slot)}
                    className={`py-2 px-3 rounded text-center text-sm ${
                      selectedTimeSlot === slot
                        ? 'bg-primary text-white'
                        : slot.is_available
                        ? 'bg-gray-700 text-white hover:bg-gray-600'
                        : 'bg-gray-800 text-gray-500 cursor-not-allowed'
                    }`}
                  >
                    {slot.start_time}
                  </button>
                ))}
              </div>
            ) : (
              <p className="text-gray-400 text-sm">No time slots available for this date</p>
            )}
          </div>
          
          <div className="form-group mb-4">
            <label htmlFor="duration" className="block text-sm font-medium text-gray-300 mb-1">
              Duration (hours)
            </label>
            <select
              id="duration"
              value={duration}
              onChange={(e) => setDuration(e.target.value)}
              className="input"
              required
            >
              <option value="1">1 hour</option>
              <option value="2">2 hours</option>
              <option value="3">3 hours</option>
              <option value="4">4 hours</option>
            </select>
          </div>
          
          <div className="form-group mb-4">
            <label className="block text-sm font-medium text-gray-300 mb-1">
              Payment Method
            </label>
            <div className="flex space-x-4">
              {isLoggedIn && (
                <label className="inline-flex items-center">
                  <input
                    type="radio"
                    value="cash"
                    checked={paymentMethod === "cash"}
                    onChange={() => setPaymentMethod("cash")}
                    className="form-radio text-primary"
                  />
                  <span className="ml-2 text-gray-300">Cash</span>
                </label>
              )}
              <label className="inline-flex items-center">
                <input
                  type="radio"
                  value="card"
                  checked={paymentMethod === "card"}
                  onChange={() => setPaymentMethod("card")}
                  className="form-radio text-primary"
                />
                <span className="ml-2 text-gray-300">Card</span>
              </label>
            </div>
          </div>
          
          {!isLoggedIn && (
            <div className="form-group mb-4">
              <label htmlFor="guest-name" className="block text-sm font-medium text-gray-300 mb-1">
                Your Name
              </label>
              <input
                id="guest-name"
                type="text"
                value={guestName}
                onChange={(e) => setGuestName(e.target.value)}
                className="input"
                placeholder="Enter your name"
                required
              />
              <p className="text-sm text-gray-400 mt-1">Required for non-logged in users</p>
            </div>
          )}
          
          <div className="mb-6">
            <div className="bg-gray-800 p-3 rounded">
              <div className="flex justify-between items-center">
                <span className="text-gray-300">Estimated Price:</span>
                <span className="text-xl font-semibold text-primary">${estimatedPrice.toFixed(2)}</span>
              </div>
              <p className="text-sm text-gray-400 mt-1">
                Based on {duration} hour{duration !== 1 ? 's' : ''} at ${club.hourly_price}/hour
              </p>
            </div>
          </div>
          
          <div className="flex justify-end">
            <button
              type="button"
              onClick={onClose}
              className="btn btn-secondary mr-2"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={!selectedTimeSlot || (!isLoggedIn && !guestName)}
            >
              Reserve
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ReservationModal;
